mapper.select:
select * 
from room_info 
where sr_seq = #{srSeq}


INSERT into room_info values
(room_seq.nextval,#{srSeq},#{name},#{info},
#{imgPath},#{person},#{price})






SELECT *
FROM room_info
WHERE room_seq = #{roomSeq}





update room_info 
set name=#{name}, info=#{info},img_path=#{imgPath},person=#{person},price=#{price}
where room_seq=#{roomSeq}






delete room_info where room_seq=#{roomSeq}
